    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
         try {
      Class.forName("com.mysql.jdbc.Driver"); 
            con=DriverManager.getConnection("jdbc:mysql://127.0.0.1/dbronaldo", "root", ""); 
                sql="insert into penjualan values ('"+
                        vfaktur.getText()+"','"+
                        tgljual+"','"+
                        vjumlah.getText()+"','"+
                        vtotal.getText()+"','"+
                        vbayar.getText()+"','"+
                        vkembali.getText()+"','"+
                        vkodebarang.getText()+"','"+
                        vharga.getText()+"','"+
                        vkodepelanggan.getText()+"','"+
                        vpelanggan.getText()+"','"+
                        jasa_kirim.getText()+"')";
                stat=con.createStatement();
                stat.execute(sql);
                tampildatapenjualan();
                clear();
                JOptionPane.showMessageDialog(rootPane, "Data Telah Tersimpan");   
            } 
            catch (SQLException | HeadlessException e) {
                JOptionPane.showMessageDialog(rootPane, "Data Gagal Disimpan" +e);
            } catch (ClassNotFoundException ex) {     
            Logger.getLogger(formpenjualan.class.getName()).log(Level.SEVERE, null, ex);
        }        
    }//GEN-LAST:event_jButton2ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // TODO add your handling code here:
        int selectedRow = tabelpenjualan.getSelectedRow(); 
    if (selectedRow != -1) {
        String no_faktur = (String) tabelpenjualan.getValueAt(selectedRow, 0); 

        try {
            Class.forName("com.mysql.jdbc.Driver"); 
            con=DriverManager.getConnection("jdbc:mysql://127.0.0.1/dbronaldo", "root", ""); 
            sql = "DELETE FROM penjualan WHERE no_faktur = '" + no_faktur  + "'";
            stat = con.createStatement();
            stat.executeUpdate(sql);

            DefaultTableModel model = (DefaultTableModel) tabelpenjualan.getModel();
            model.removeRow(selectedRow);
            JOptionPane.showMessageDialog(rootPane, "Data Berhasil di Hapus");
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "Data gagal dihapus " + e);
        }
    } else {
        JOptionPane.showMessageDialog(rootPane, "Pilih salah satu data untuk dihapus.");
    }   
    }//GEN-LAST:event_jButton3ActionPerformed

