    private void tampildatapenjualan() {
        DefaultTableModel tabel = new  DefaultTableModel();
        tabel.addColumn("No Faktur");
        tabel.addColumn("Tanggal Jual");
        tabel.addColumn("Jumlah Jual");
        tabel.addColumn("Total Bayar");
        tabel.addColumn("Bayar");
        tabel.addColumn("Uang Kembali");
        tabel.addColumn("Kode Barang");
        tabel.addColumn("Harga");
        tabel.addColumn("Kode Pelanggan");
        tabel.addColumn("Nama Pelanggan");
        tabel.addColumn("Jasa Kirim");
        
        try {
            Class.forName("com.mysql.jdbc.Driver"); 
            con=DriverManager.getConnection("jdbc:mysql://127.0.0.1/dbronaldo", "root", ""); 
            sql="select * from penjualan";
            stat= con.createStatement();
            rs= stat.executeQuery(sql);
            while (rs.next()) {
              tabel.addRow(new Object[]{
                  rs.getString(1),rs.getString(2),
                  rs.getString(3), rs.getString(4),
                  rs.getString(5), rs.getString(6),
                  rs.getString(7), rs.getString(8),
                  rs.getString(9),rs.getString(10),
                  rs.getString(11)
              });
              tabelpenjualan.setModel(tabel);
                
            }
        
        } catch (Exception e) {
            JOptionPane.showMessageDialog(rootPane, "error"+e);
        }
    }

